import React from "react";

function App() {
  var status = true;
  function clicked() {
    var varriable = document.getElementById("testtext");
    if (status == true) {
      status == false;
    }
    if (status == true) varriable.style.color = "red";
    else varriable.style.color = "green";
  }
  return (
    <>
      <h1 id="testtext">i am a text</h1>
      <button id="myButton" onClick={clicked}>
        press me to alert
      </button>
    </>
  );
}
export default App;
